import React, { useState } from 'react'
import { useEffect } from 'react';
import StoredComments from './StoredComments';

export default function InputComments() {

    const [text, setText] = useState("")
    const [comment, setComment] = useState([]);

    
    
    let changeFunc = (e)=> {
        setText(e.target.value)
    }
    let postFunc = ()=> {
        console.log("POST");

        let temp = [];
        temp = JSON.parse(localStorage.getItem('ac8760a110c384'));
        const newComment = {username:"SSS", body:text}
        temp.push(newComment);
        console.log("TEMP IS: ", temp)

        useEffect(() => {
            console.log("Effect agya")
            localStorage.setItem('myKey', JSON.stringify(temp))
        }, [setComment])

        // setComment(comment)
        setText('')
    }

    return (
        <>
        <h1>Comment Section</h1>
        <input type="text" placeholder="Enter Comment" value={text} onChange={changeFunc} />
        <button onClick={postFunc}>POST</button>
        </>
    )
}
