import React, { useState } from 'react'
import { useEffect } from 'react';
import Comments from './Comments';


export default function StoredComments() {

    const [backendComments, setbackendComments] = useState([]);
    console.log("Type BC:",typeof(backendComments))
    console.log("Backend Comments : ", backendComments)

    useEffect(() => {
        for (let i=0; i<localStorage.length; i++){
            let myKey = localStorage.key(i);
            let myObj = JSON.parse(localStorage.getItem(myKey));
            console.log("Type of myObj:",typeof(myObj))
            //console.log("Setting backend comments, number", i+1)
            setbackendComments(backendComments.push(myObj))
            setbackendComments([...backendComments, {username:myObj.username, body:myObj.body}])
            // console.log("Type BC:",typeof(backendComments))
        }
    
    }, []);
    
    return (
        <>
            <p>
                {/* {backendComments.map((backendComment) => (
                    <div>{backendComment.body}</div>
                ))} */}
            </p>
        </>
    )
}
