import React, { useState } from 'react'
import { useEffect } from 'react';
import Comments from './Comments';


export default function StoredComments() {

    const [backendComments, setbackendComments] = useState([]);
    console.log("Early type:", typeof(backendComments));

    useEffect(() => {
        let newArr = [];
        //console.log("Before for:", newArr)
        for (let i=0; i<localStorage.length; i++){
            let myKey = localStorage.key(i);
            let myObj = JSON.parse(localStorage.getItem(myKey));
            //setbackendComments(backendComments.push(myObj))
            newArr.push(myObj);
            // setbackendComments([...backendComments, {username:myObj.username, body:myObj.body}]);
            // console.log("Type BC2:",typeof(backendComments))
        }
        //console.log("After f0r: ", newArr)
        // console.log("Before: ",backendComments)
        setbackendComments(newArr)  
    }, []);
    console.log("After type:", typeof(backendComments));
    // console.log("DONE: ",backendComments)
    
    return (
        <>
            <p>
                {/* {backendComments.map((backendComment) => (
                    <span>{console.log("HTML:", backendComment[0].body)}</span>
                ))} */}

                {/* {backendComments.map( (backendComment)=> (
                    <span>{backendComment}</span>
                ))} */}

                {/* {backendComments.map((backendComment,i) => <li key={i}>{backendComment.length}</li>)} */}
            </p>
        </>
    )
}
