import React, { useState } from 'react'
import { useEffect } from 'react';
import Comments from './Comments';


export default function StoredComments() {

    const [backendComments, setbackendComments] = useState([refreshMemory()]);
    useEffect(() => {
        let myObj = [];
        myObj = JSON.parse(localStorage.getItem('ac8760a110c384'));
        backendComments.push(myObj);
        setbackendComments(myObj)
    }, []);

    let refreshMemory = ()=> {
        let val = localStorage.getItem('myKey');
        if(val){
            return JSON.parse(localStorage.getItem('myKey'))
        }
        else {
            return [];
        }
    }
    
    return (
        <>
            {backendComments.map((backendComment)=>{
                return(
                    <Comments key='0' commentId={backendComment.id} commentBody={backendComment.body}/>
                )
            })}
        </>
    )
}
