import { useEffect } from "react";
import { useState } from "react";

function App() {

  const [comment, setComment] = useState("")
  let changeFunc = (e)=> {
    setComment(e.target.value)
  }

  const [allComments, setAllComments] = useState([])
  let postFunc = ()=> {
    // setAllComments( (allComments) => [...allComments, comment]) //https://bobbyhadz.com/blog/react-push-to-state-array
    setAllComments( [comment].concat(allComments)); //https://javascript.plainenglish.io/how-to-add-to-an-array-in-react-state-3d08ddb2e1dc

    // console.log(document.getElementById('inp').value)
    // document.getElementById('inp').value = '';
    // console.log(document.getElementById('inp').value)
    setComment('');
  }

  useEffect(() => {
    console.log("Inside UseEffect")
    // let existingComments = JSON.parse(localStorage.getItem('myKey:'))
    let existingComments = localStorage.getItem('myKey')
    console.log("HOLA " + existingComments)
    let val = JSON.stringify(allComments)
    // existingComments.push(val)
    // console.log("HOLA2 " + existingComments)
    console.log(typeof(val))
    console.log(typeof(existingComments))

    console.log(val)

    // if(val){
    //   existingComments.push(val)
    // }


    // let uniqueId = Math.random().toString(16).slice(2);
    // if(allComments.length>0){
    //   //localStorage.setItem('myKey', val);

    // }

  }, [allComments]);

  return (
    <> 
      <h1>Comment Section</h1>
      <textarea id="inp" placeholder="Enter Comment" value={comment} onChange={changeFunc} cols="50" rows="2"></textarea>
      <button onClick={postFunc}>POST</button>

      {/* https://www.freecodecamp.org/news/javascript-map-how-to-use-the-js-map-function-array-method/ */}
      {allComments.map( (text)=> (
        <p key={text}>{text}</p>
      ))}

    </>
  );
}

export default App;
