import { useEffect } from "react";
import { useState } from "react";

function App() {

  const [comment, setComment] = useState("")
  let changeFunc = (e)=> {
    setComment(e.target.value)
  }

  const [allComments, setAllComments] = useState()
  let postFunc = ()=> {
    setAllComments( (allComments)=> 
      [...allComments, comment]
    )
  }

  // useEffect(() => {
  //   localStorage.setItem('AAA', 'BBB');
  // }, [allComments])

  return (
    <> 
      <h1>Comment Section</h1>
      <textarea value={comment} onChange={changeFunc} cols="50" rows="2"></textarea>
      <button onClick={postFunc}>POST</button>

      {/* https://www.freecodecamp.org/news/javascript-map-how-to-use-the-js-map-function-array-method/ */}
      {allComments.map( (element)=> (
        <p>{element}</p>
      ))}

    </>
  );
}

export default App;
