import { useEffect } from "react";
import { useState } from "react";
import OldComments from "./HarjasComponents/StoredComments";

function App() {

  const [comment, setComment] = useState("")
  let changeFunc = (e)=> {
    setComment(e.target.value)
  }

  // const [allComments, setAllComments] = useState({
  //   commentValue:"",
  //   commentLikes:0,
  //   commentDislikes:0
  // })

  let postFunc = ()=> {
    // setAllComments( (allComments) => [...allComments, comment]) //https://bobbyhadz.com/blog/react-push-to-state-array
    // setAllComments( [comment].concat(allComments)); //https://javascript.plainenglish.io/how-to-add-to-an-array-in-react-state-3d08ddb2e1dc

    let myLi = document.createElement('li');
    myLi.id = 'a'+Math.random().toString(16).slice(2);
    myLi.innerText = "OKK";
    document.getElementById('myUl').appendChild(myLi)

    setComment('');

    localStorage.setItem(myLi.id, JSON.stringify(myLi.innerText));
  }

  return (
    <> 
      <h1>Comment Section</h1>
      <textarea id="inp" placeholder="Enter Comment" value={comment} onChange={changeFunc} cols="50" rows="2"></textarea>
      <button onClick={postFunc}>POST</button>

      <ul id="myUl"></ul>

      <StoredComments/>

      {/* https://www.freecodecamp.org/news/javascript-map-how-to-use-the-js-map-function-array-method/ */}
      {/* {allComments.map( (text)=> (
        <p key={text}>{text}</p
      ))} */}

    </>
  );
}

export default App;
